/***
 * Print quote function
***/
function printQuote() {
    let randomQuote = getRandomQuote();
    let html = `
    <div class="section-container">
    <span class="img">
        <img class="container-image" src="${randomQuote.img}" alt="${randomQuote.source}">
    </span>
    <p class="quote text">${randomQuote.quote}</p>
    <p class="source m-3 text">${randomQuote.source}`;

    if (randomQuote.citation) {
        html += `<span class="citation text">${randomQuote.citation}</span>`;
        if (randomQuote.year) {
            html += `<span class="year text">${randomQuote.year}</span>`;
        }
        if (randomQuote.type) {
            html += `<span class="type text">${randomQuote.type}</span>`;
        }

        html += `</div>`;
        
    }

    console.log(html)
    // console.log(setRandomColor());

    // setRandomColor();
     document.getElementById('quote-box').innerHTML = html;
}


setInterval(printQuote, 10000);



